import React, { createContext, useState, useContext, ReactNode, Dispatch, SetStateAction } from 'react';

export type Language = 'ja' | 'en';

interface LocaleContextType {
  language: Language;
  setLanguage: Dispatch<SetStateAction<Language>>;
}

const LocaleContext = createContext<LocaleContextType | undefined>(undefined);

export const LocaleProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [language, setLanguage] = useState<Language>('ja');

  return (
    <LocaleContext.Provider value={{ language, setLanguage }}>
      {children}
    </LocaleContext.Provider>
  );
};

export const useLanguage = () => {
  const context = useContext(LocaleContext);
  if (context === undefined) {
    throw new Error('useLanguage must be used within a LocaleProvider');
  }
  return context;
};

export const parseUTCDateString = (dateStr: string): Date | null => {
    if (!/^\d{4}\/\d{2}\/\d{2}$/.test(dateStr)) return null;
    const parts = dateStr.split('/').map(p => parseInt(p, 10));
    const date = new Date(Date.UTC(parts[0], parts[1] - 1, parts[2]));
    if (date.getUTCFullYear() !== parts[0] || date.getUTCMonth() !== parts[1] - 1 || date.getUTCDate() !== parts[2]) {
        return null;
    }
    return date;
};

export const formatDateUTC = (date: Date): string => {
  const year = date.getUTCFullYear();
  const month = (date.getUTCMonth() + 1).toString().padStart(2, '0');
  const day = date.getUTCDate().toString().padStart(2, '0');
  return `${year}/${month}/${day}`;
};

export const addDaysUTC = (date: Date, days: number): Date => {
    const result = new Date(date.getTime());
    result.setUTCDate(result.getUTCDate() + days);
    return result;
};

export const getDatesInRange = (startDate: Date, endDate: Date): Date[] => {
  const dates: Date[] = [];
  const currentDate = new Date(startDate.getTime());

  while (currentDate.getTime() <= endDate.getTime()) {
    dates.push(new Date(currentDate.getTime()));
    currentDate.setUTCDate(currentDate.getUTCDate() + 1);
  }
  return dates;
};

export const calculateWorkingDays = (startDate: Date, endDate: Date, nonWorkingDays: Set<number>): number => {
    if (endDate < startDate) return 0;
    let count = 0;
    const current = new Date(startDate.getTime());
    while (current.getTime() <= endDate.getTime()) {
      if (!nonWorkingDays.has(current.getUTCDay())) {
        count++;
      }
      current.setUTCDate(current.getUTCDate() + 1);
    }
    return count;
};

export const addWorkingDays = (startDate: Date, days: number, nonWorkingDays: Set<number>): Date => {
    if (days === 0) {
        let tempDate = new Date(startDate.getTime());
        while(nonWorkingDays.has(tempDate.getUTCDay())) {
             tempDate.setUTCDate(tempDate.getUTCDate() - 1);
        }
        return tempDate;
    };
    if (days < 1) return startDate;

    const result = new Date(startDate.getTime());
    let added = 0;

    while(nonWorkingDays.has(result.getUTCDay())){
      result.setUTCDate(result.getUTCDate() + 1);
    }

    while (added < days -1) {
      result.setUTCDate(result.getUTCDate() + 1);
      if (!nonWorkingDays.has(result.getUTCDay())) {
        added++;
      }
    }
    return result;
};

export const addOrSubtractWorkingDays = (startDate: Date, days: number, nonWorkingDays: Set<number>): Date => {
    const result = new Date(startDate.getTime());
    let remaining = Math.abs(days);
    const direction = Math.sign(days);

    if(direction === 0) return result;

    while (remaining > 0) {
      result.setUTCDate(result.getUTCDate() + direction);
      if (!nonWorkingDays.has(result.getUTCDay())) {
        remaining--;
      }
    }
    return result;
};

import React, { useState, useMemo } from 'react';
import { useLanguage } from '../contexts/LocaleContext.tsx';

interface DatePickerProps {
  initialDate?: string; 
  taskStartDate?: string;
  taskEndDate?: string;
  onSelectDate: (date: string) => void;
  onClose: () => void;
  minDate?: string;
  maxDate?: string;
  showToday?: boolean;
  position?: 'top' | 'bottom';
}

const parseUTCDateString = (dateStr: string): Date | null => {
    if (!/^\d{4}\/\d{2}\/\d{2}$/.test(dateStr)) return null;
    const parts = dateStr.split('/').map(p => parseInt(p, 10));
    const date = new Date(Date.UTC(parts[0], parts[1] - 1, parts[2]));
    if (date.getUTCFullYear() !== parts[0] || date.getUTCMonth() !== parts[1] - 1 || date.getUTCDate() !== parts[2]) {
        return null;
    }
    return date;
};

const formatDateUTC = (date: Date): string => {
  const year = date.getUTCFullYear();
  const month = (date.getUTCMonth() + 1).toString().padStart(2, '0');
  const day = date.getUTCDate().toString().padStart(2, '0');
  return `${year}/${month}/${day}`;
};

const DAYS_OF_WEEK = {
    en: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
    ja: ['日', '月', '火', '水', '木', '金', '土'],
}

const DatePicker: React.FC<DatePickerProps> = ({ initialDate, taskStartDate, taskEndDate, onSelectDate, minDate, maxDate, showToday = false, position = 'bottom' }) => {
  const { language } = useLanguage();

  const getInitialDisplayDate = () => {
    if (initialDate) {
        const parsedDate = parseUTCDateString(initialDate);
        if (parsedDate) return parsedDate;
    }
    const now = new Date();
    return new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
  };

  const [displayDate, setDisplayDate] = useState(getInitialDisplayDate);

  const selectedDate = useMemo(() => {
    return initialDate ? parseUTCDateString(initialDate) : null;
  }, [initialDate]);

  const taskStart = useMemo(() => taskStartDate ? parseUTCDateString(taskStartDate) : null, [taskStartDate]);
  const taskEnd = useMemo(() => taskEndDate ? parseUTCDateString(taskEndDate) : null, [taskEndDate]);

  const minDateTime = useMemo(() => minDate ? parseUTCDateString(minDate)?.getTime() : -Infinity, [minDate]);
  const maxDateTime = useMemo(() => maxDate ? parseUTCDateString(maxDate)?.getTime() : Infinity, [maxDate]);

  const today = useMemo(() => {
    if (!showToday) return null;
    const now = new Date();
    return new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
  }, [showToday]);


  const changeMonth = (amount: number) => {
    setDisplayDate(prev => {
        const newDate = new Date(prev.getTime());
        newDate.setUTCMonth(newDate.getUTCMonth() + amount);
        return newDate;
    });
  };

  const calendarGrid = useMemo(() => {
    const year = displayDate.getUTCFullYear();
    const month = displayDate.getUTCMonth();

    const firstDayOfMonth = new Date(Date.UTC(year, month, 1)).getUTCDay();
    const daysInMonth = new Date(Date.UTC(year, month + 1, 0)).getUTCDate();

    const days: (Date|null)[] = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
        days.push(null);
    }
    for (let i = 1; i <= daysInMonth; i++) {
        days.push(new Date(Date.UTC(year, month, i)));
    }
    return days;
  }, [displayDate]);

  const handleDateClick = (date: Date) => {
      onSelectDate(formatDateUTC(date));
  };

  const positionClasses = position === 'top' ? 'bottom-full mb-2' : 'top-full mt-2';

  return (
    <div data-calendar-popover className={`absolute ${positionClasses} w-72 bg-white border border-gray-300 rounded-lg shadow-xl z-40 p-2 font-sans`}>
      <div className="flex justify-between items-center mb-2 px-2">
        <button onClick={() => changeMonth(-1)} className="p-1 rounded-full hover:bg-gray-100" aria-label="Previous month">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"></path></svg>
        </button>
        <div className="font-semibold text-sm text-gray-700">
            {displayDate.toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US', { year: 'numeric', month: 'long', timeZone: 'UTC' })}
        </div>
        <button onClick={() => changeMonth(1)} className="p-1 rounded-full hover:bg-gray-100" aria-label="Next month">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7"></path></svg>
        </button>
      </div>
      <div className="grid grid-cols-7 gap-1 text-center text-xs text-gray-500 mb-1">
        {DAYS_OF_WEEK[language].map(day => <div key={day}>{day}</div>)}
      </div>
      <div className="grid grid-cols-7 gap-1">
        {calendarGrid.map((date, index) => {
            if (!date) {
                return <div key={index}></div>;
            }

            const dayTime = date.getTime();
            const isSelected = selectedDate && dayTime === selectedDate.getTime();
            const isToday = today && dayTime === today.getTime();
            const isDisabled = dayTime < minDateTime || dayTime > maxDateTime;

            const classNames = ['w-full', 'h-9', 'text-sm', 'transition-colors', 'relative', 'flex', 'items-center', 'justify-center'];

            let inRange = false;
            if (taskStart && taskEnd) {
                const startTime = taskStart.getTime();
                const endTime = taskEnd.getTime();
                inRange = dayTime >= startTime && dayTime <= endTime;
            }

            if (isDisabled) {
                classNames.push('text-gray-300', 'cursor-not-allowed');
            } else {
                if (isSelected) {
                    classNames.push('bg-indigo-600', 'text-white', 'rounded-full');
                } else if (inRange) {
                    classNames.push('bg-indigo-100');
                    const startTime = taskStart.getTime();
                    const endTime = taskEnd.getTime();
                    if (startTime === endTime) {
                        classNames.push('rounded-full');
                    } else if (dayTime === startTime) {
                        classNames.push('rounded-l-full');
                    } else if (dayTime === endTime) {
                        classNames.push('rounded-r-full');
                    }
                } else {
                     classNames.push('hover:bg-gray-200', 'rounded-full');
                }

                if (isToday && !isSelected) {
                    classNames.push('ring-2', 'ring-blue-500');
                }
            }

            return (
                <div key={index} className="flex justify-center items-center">
                    <button 
                        onClick={() => !isDisabled && handleDateClick(date)}
                        disabled={isDisabled}
                        className={classNames.join(' ')}
                    >
                        <span className="relative">{date.getUTCDate()}</span>
                    </button>
                </div>
            )
        })}
      </div>
    </div>
  );
};

export default DatePicker;

import React from 'react';
import { useLanguage } from '../contexts/LocaleContext.tsx';
import { translations, TranslationKey } from '../lib/locales.ts';

interface SettingsDialogProps {
  onClose: () => void;
  holidays: Set<number>;
  setHolidays: React.Dispatch<React.SetStateAction<Set<number>>>;
  columnVisibility: {
    assignee: boolean;
    startDate: boolean;
    endDate: boolean;
    duration: boolean;
    progress: boolean;
    manHours: boolean;
  };
  setColumnVisibility: React.Dispatch<React.SetStateAction<any>>;
  baseColor: string;
  setBaseColor: React.Dispatch<React.SetStateAction<string>>;
  progressColor: string;
  setProgressColor: React.Dispatch<React.SetStateAction<string>>;
  textColor: string;
  setTextColor: React.Dispatch<React.SetStateAction<string>>;
  progressLineColor: string;
  setProgressLineColor: React.Dispatch<React.SetStateAction<string>>;
  rowHeight: number;
  setRowHeight: React.Dispatch<React.SetStateAction<number>>;
  onResetColors: () => void;
}

const SettingsDialog: React.FC<SettingsDialogProps> = ({
  onClose,
  holidays,
  setHolidays,
  columnVisibility,
  setColumnVisibility,
  baseColor,
  setBaseColor,
  progressColor,
  setProgressColor,
  textColor,
  setTextColor,
  progressLineColor,
  setProgressLineColor,
  rowHeight,
  setRowHeight,
  onResetColors,
}) => {
  const { language } = useLanguage();
  const t = (key: TranslationKey) => translations[key][language];

  const handleHolidayChange = (dayIndex: number) => {
    setHolidays(prev => {
      const newHolidays = new Set(prev);
      if (newHolidays.has(dayIndex)) {
        newHolidays.delete(dayIndex);
      } else {
        newHolidays.add(dayIndex);
      }
      return newHolidays;
    });
  };

  const handleColumnVisibilityChange = (column: keyof typeof columnVisibility) => {
    setColumnVisibility((prev: any) => ({
      ...prev,
      [column]: !prev[column],
    }));
  };

  const handleInputFocus = (e: React.FocusEvent<HTMLInputElement>) => {
    e.target.select();
  };

  const daysOfWeek: { key: TranslationKey, index: number }[] = [
    { key: 'sun', index: 0 },
    { key: 'mon', index: 1 },
    { key: 'tue', index: 2 },
    { key: 'wed', index: 3 },
    { key: 'thu', index: 4 },
    { key: 'fri', index: 5 },
    { key: 'sat', index: 6 },
  ];

  const columns: (keyof typeof columnVisibility)[] = ['assignee', 'startDate', 'endDate', 'duration', 'progress', 'manHours'];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={onClose}>
      <div className="bg-white rounded-lg shadow-xl w-full max-w-md m-4" onClick={e => e.stopPropagation()}>
        <div className="p-6 border-b">
          <h2 className="text-xl font-bold text-slate-800">{t('settings')}</h2>
        </div>
        <div className="p-6 space-y-6">
          <div>
            <h3 className="text-lg font-semibold text-slate-700">{t('holidaySettings')}</h3>
            <p className="text-sm text-slate-500 mb-3">{t('holidaySettingsDesc')}</p>
            <div className="grid grid-cols-2 sm:grid-cols-4 gap-3">
              {daysOfWeek.map(({ key, index }) => {
                const dayColorClass = index === 0 ? 'text-red-600' : index === 6 ? 'text-blue-600' : 'text-slate-600';
                return (
                  <label key={key} className="flex items-center space-x-2 p-2 rounded-md hover:bg-gray-100 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={holidays.has(index)}
                      onChange={() => handleHolidayChange(index)}
                      className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                    />
                    <span className={`text-sm ${dayColorClass}`}>{t(key)}</span>
                  </label>
                );
              })}
            </div>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-slate-700">{t('displaySettings')}</h3>
            <p className="text-sm text-slate-500 mb-3">{t('displaySettingsDesc')}</p>
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
              {columns.map(column => (
                <label key={column} className="flex items-center space-x-2 p-2 rounded-md hover:bg-gray-100 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={columnVisibility[column]}
                    onChange={() => handleColumnVisibilityChange(column)}
                    className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                  />
                  <span className="text-sm text-slate-600 capitalize">{t(column as TranslationKey)}</span>
                </label>
              ))}
            </div>
          </div>
          <div>
            <div className="flex justify-between items-center">
              <h3 className="text-lg font-semibold text-slate-700">{t('colorSettings')}</h3>
              <button onClick={onResetColors} className="text-xs font-medium text-indigo-600 hover:text-indigo-800 transition-colors">
                {t('resetToDefaults')}
              </button>
            </div>
            <div className="mt-3 space-y-4">
              <div className="flex items-center justify-between">
                <label className="text-sm text-slate-600">{t('baseColor')}</label>
                <div className="flex items-center gap-2">
                  <input type="text" value={baseColor} onChange={(e) => setBaseColor(e.target.value)} onFocus={handleInputFocus} className="w-24 px-2 py-1 border border-gray-300 rounded-md shadow-sm text-sm focus:ring-indigo-500 focus:border-indigo-500" />
                  <input type="color" value={baseColor} onChange={(e) => setBaseColor(e.target.value)} className="p-1 h-8 w-10 block bg-white border border-gray-300 rounded-md cursor-pointer" />
                </div>
              </div>
              <div className="flex items-center justify-between">
                <label className="text-sm text-slate-600">{t('progressColor')}</label>
                <div className="flex items-center gap-2">
                  <input type="text" value={progressColor} onChange={(e) => setProgressColor(e.target.value)} onFocus={handleInputFocus} className="w-24 px-2 py-1 border border-gray-300 rounded-md shadow-sm text-sm focus:ring-indigo-500 focus:border-indigo-500" />
                  <input type="color" value={progressColor} onChange={(e) => setProgressColor(e.target.value)} className="p-1 h-8 w-10 block bg-white border border-gray-300 rounded-md cursor-pointer" />
                </div>
              </div>
              <div className="flex items-center justify-between">
                <label className="text-sm text-slate-600">{t('textColor')}</label>
                <div className="flex items-center gap-2">
                  <input type="text" value={textColor} onChange={(e) => setTextColor(e.target.value)} onFocus={handleInputFocus} className="w-24 px-2 py-1 border border-gray-300 rounded-md shadow-sm text-sm focus:ring-indigo-500 focus:border-indigo-500" />
                  <input type="color" value={textColor} onChange={(e) => setTextColor(e.target.value)} className="p-1 h-8 w-10 block bg-white border border-gray-300 rounded-md cursor-pointer" />
                </div>
              </div>
              <div className="flex items-center justify-between">
                <label className="text-sm text-slate-600">{t('progressLineColor')}</label>
                <div className="flex items-center gap-2">
                  <input type="text" value={progressLineColor} onChange={(e) => setProgressLineColor(e.target.value)} onFocus={handleInputFocus} className="w-24 px-2 py-1 border border-gray-300 rounded-md shadow-sm text-sm focus:ring-indigo-500 focus:border-indigo-500" />
                  <input type="color" value={progressLineColor} onChange={(e) => setProgressLineColor(e.target.value)} className="p-1 h-8 w-10 block bg-white border border-gray-300 rounded-md cursor-pointer" />
                </div>
              </div>
            </div>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-slate-700">{t('rowHeightSettings')}</h3>
            <p className="text-sm text-slate-500 mb-3">{t('rowHeightSettingsDesc')}</p>
            <div className="flex items-center space-x-4">
               <label className="text-sm text-slate-600">{t('rowHeight')}</label>
              <input type="range" min="40" max="80" value={rowHeight} onChange={(e) => setRowHeight(Number(e.target.value))} className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer" />
              <span className="text-sm font-medium text-slate-600 w-12 text-center">{rowHeight}px</span>
            </div>
          </div>
        </div>
        <div className="p-4 bg-slate-50 text-right rounded-b-lg">
          <button onClick={onClose} className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">{t('close')}</button>
        </div>
      </div>
    </div>
  );
};

