export default ConfirmDialog;

import React, { useState, useEffect, useRef, useCallback } from 'react';
import Timeline from './components/Timeline.tsx';
import { useLanguage } from './contexts/LocaleContext.tsx';
import { translations, TranslationKey } from './lib/locales.ts';
import { SettingsIcon, ZoomInIcon, ZoomOutIcon, ProgressLineIcon, XIcon, DownloadIcon, UploadIcon } from './components/Icons.tsx';
import SettingsDialog from './components/SettingsDialog.tsx';
import DatePicker from './components/DatePicker.tsx';
import { Task } from './types.ts';
import { formatDateUTC, addDaysUTC, parseUTCDateString, addWorkingDays, addOrSubtractWorkingDays, calculateWorkingDays } from './lib/utils.ts';
import ConfirmDialog from './components/ConfirmDialog.tsx';

const ZOOM_LEVELS = [8, 12, 18, 24, 40, 64];

const getInitialStartDate = () => {
    const now = new Date();
    const todayUTC = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
    return formatDateUTC(todayUTC);
};

const getInitialEndDate = () => {
    const now = new Date();
    const todayUTC = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
    const futureDate = addDaysUTC(todayUTC, 60);
    return formatDateUTC(futureDate);
};

const INITIAL_COLORS = {
    base: '#fef08a',
    progress: '#bae6fd',
    text: '#000000',
    progressLine: '#ff0000',
};

const Main: React.FC = () => {
  const { language, setLanguage } = useLanguage();
  const t = useCallback((key: TranslationKey) => {
    return translations[key][language];
  }, [language]);

  const [isSettingsModalOpen, setIsSettingsModalOpen] = useState(false);
  const [confirmModalState, setConfirmModalState] = useState<{ isOpen: boolean; title: string; message: string; onConfirm: () => void; }>({
    isOpen: false, title: '', message: '', onConfirm: () => {},
  });
  const [holidays, setHolidays] = useState<Set<number>>(() => new Set([0, 6]));
  const [columnVisibility, setColumnVisibility] = useState({
    assignee: true, startDate: false, endDate: false, duration: true, progress: true, manHours: false,
  });
  const [zoomIndex, setZoomIndex] = useState(3);
  const [progressLineDate, setProgressLineDate] = useState<string | null>(null);
  const [isProgressLineCalendarOpen, setIsProgressLineCalendarOpen] = useState(false);
  const importFileRef = useRef<HTMLInputElement>(null);
  const [rowHeight, setRowHeight] = useState<number>(40);

  const [projectName, setProjectName] = useState<string>(() => translations.untitledProject.ja);
  const [projectStart, setProjectStart] = useState<string>(getInitialStartDate());
  const [projectEnd, setProjectEnd] = useState<string>(getInitialEndDate());
  const [creationDate, setCreationDate] = useState<string>(getInitialStartDate());
  const [creatorName, setCreatorName] = useState<string>('');

  const [baseColor, setBaseColor] = useState<string>(INITIAL_COLORS.base);
  const [progressColor, setProgressColor] = useState<string>(INITIAL_COLORS.progress);
  const [textColor, setTextColor] = useState<string>(INITIAL_COLORS.text);
  const [progressLineColor, setProgressLineColor] = useState<string>(INITIAL_COLORS.progressLine);

  const [tasks, setTasks] = useState<Task[]>(() => {
    const initialT = (key: TranslationKey) => translations[key]['ja'];
    return [
      { id: '1', name: `${initialT('newTaskName')} 1`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
      { id: '2', name: `${initialT('newTaskName')} 2`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
      { id: '3', name: `${initialT('newTaskName')} 3`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
    ];
  });

  const handleZoomIn = () => setZoomIndex(prev => Math.min(prev + 1, ZOOM_LEVELS.length - 1));
  const handleZoomOut = () => setZoomIndex(prev => Math.max(prev - 1, 0));

  const toggleLanguage = () => {
    setLanguage(lang => (lang === 'ja' ? 'en' : 'ja'));
  };

  const handleResetColors = () => {
    setBaseColor(INITIAL_COLORS.base);
    setProgressColor(INITIAL_COLORS.progress);
    setTextColor(INITIAL_COLORS.text);
    setProgressLineColor(INITIAL_COLORS.progressLine);
  };

  const handleDeleteAllTasks = () => {
    if (tasks.length === 0) return;
    setConfirmModalState({
        isOpen: true,
        title: t('deleteAllTasks'),
        message: t('confirmDeleteAllTasks'),
        onConfirm: () => {
            setTasks([]);
            setConfirmModalState({ isOpen: false, title: '', message: '', onConfirm: () => {} });
        },
    });
  };

  const handleAddTask = () => {
    const newId = Date.now().toString();
    setTasks(prev => [...prev, { id: newId, name: `${t('newTaskName')} ${prev.length + 1}`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 }]);
  };

  const handleDeleteTask = (id: string) => {
    setTasks(prev => prev.filter(task => task.id !== id));
  };

  const handleDuplicateTask = (taskId: string) => {
    setTasks(currentTasks => {
      const taskIndex = currentTasks.findIndex(t => t.id === taskId);
      if (taskIndex === -1) return currentTasks;
      const originalTask = currentTasks[taskIndex];
      const newTask: Task = {
        ...originalTask,
        id: Date.now().toString(),
        name: `${originalTask.name}${t('copySuffix')}`,
      };
      const newTasks = [...currentTasks];
      newTasks.splice(taskIndex + 1, 0, newTask);
      return newTasks;
    });
  };

  const handleTaskChange = (id: string, field: keyof Task, value: string | number | undefined) => {
    setTasks(prev => prev.map(task => task.id === id ? { ...task, [field]: value } : task));
  };

  const handleDurationChange = (task: Task, newDurationStr: string) => {
    const newDuration = parseInt(newDurationStr, 10);
    if (isNaN(newDuration) || newDuration < 1) return;
    const startDate = parseUTCDateString(task.startDate);
    if (startDate) {
        const newEndDate = addWorkingDays(startDate, newDuration, holidays);
        handleTaskChange(task.id, 'endDate', formatDateUTC(newEndDate));
    }
  };

  const handleProgressChange = (taskId: string, newProgressStr: string) => {
    let newProgress = parseInt(newProgressStr, 10);
    if (isNaN(newProgress)) newProgress = 0;
    const clampedProgress = Math.max(0, Math.min(100, newProgress));
    handleTaskChange(taskId, 'progress', clampedProgress);
  };

  const handleManHoursChange = (taskId: string, newManHoursStr: string) => {
    let newManHours = parseFloat(newManHoursStr);
    if (isNaN(newManHours)) {
      handleTaskChange(taskId, 'manHours', undefined);
      return;
    }
    const clampedManHours = Math.max(0, newManHours);
    handleTaskChange(taskId, 'manHours', clampedManHours);
  };

  const handleTaskDateSet = (taskId: string, startDateStr: string, endDateStr: string) => {
      const d1 = parseUTCDateString(startDateStr);
      const d2 = parseUTCDateString(endDateStr);
      if (d1 && d2) {
          const finalStartDate = d1.getTime() < d2.getTime() ? startDateStr : endDateStr;
          const finalEndDate = d1.getTime() < d2.getTime() ? endDateStr : startDateStr;
          setTasks(prev => prev.map(task => task.id === taskId ? { ...task, startDate: finalStartDate, endDate: finalEndDate } : task));
      }
  };

  const handleTaskDragUpdate = (taskId: string, actionType: 'move' | 'resize-start' | 'resize-end', initialStartDate: Date, initialEndDate: Date, dayOffset: number) => {
      const pStart = parseUTCDateString(projectStart);
      const pEnd = parseUTCDateString(projectEnd);
      if (!pStart || !pEnd) return;

      setTasks(currentTasks =>
        currentTasks.map(task => {
          if (task.id === taskId) {
            let newStartDate: Date;
            let newEndDate: Date;
            switch (actionType) {
              case 'move': {
                const durationInWorkingDays = calculateWorkingDays(initialStartDate, initialEndDate, holidays) || 1;
                let potentialStartDate = addDaysUTC(initialStartDate, dayOffset);
                if (potentialStartDate.getTime() < pStart.getTime()) potentialStartDate = pStart;
                let potentialEndDate = addWorkingDays(potentialStartDate, durationInWorkingDays, holidays);
                if (potentialEndDate.getTime() > pEnd.getTime()) {
                  potentialEndDate = pEnd;
                  potentialStartDate = addOrSubtractWorkingDays(potentialEndDate, -(durationInWorkingDays - 1), holidays);
                  if (potentialStartDate.getTime() < pStart.getTime()) potentialStartDate = pStart;
                }
                newStartDate = potentialStartDate;
                newEndDate = addWorkingDays(newStartDate, durationInWorkingDays, holidays);
                if (newEndDate.getTime() > pEnd.getTime()) newEndDate = pEnd;
                if (newEndDate < newStartDate) newEndDate = newStartDate;
                break;
              }
              case 'resize-start': {
                newStartDate = addDaysUTC(initialStartDate, dayOffset);
                newEndDate = initialEndDate;
                if (newStartDate.getTime() < pStart.getTime()) newStartDate = pStart;
                if (newStartDate.getTime() > newEndDate.getTime()) [newStartDate, newEndDate] = [newEndDate, newStartDate];
                break;
              }
              case 'resize-end': {
                newStartDate = initialStartDate;
                newEndDate = addDaysUTC(initialEndDate, dayOffset);
                if (newEndDate.getTime() > pEnd.getTime()) newEndDate = pEnd;
                if (newEndDate.getTime() < newStartDate.getTime()) [newEndDate, newStartDate] = [newEndDate, newStartDate];
                break;
              }
            }
            return { ...task, startDate: formatDateUTC(newStartDate), endDate: formatDateUTC(newEndDate) };
          }
          return task;
        })
      );
  };

  const handleTaskReorder = (draggedTaskId: string, dropIndex: number) => {
      setTasks(currentTasks => {
          const newTasks = [...currentTasks];
          const draggedTask = newTasks.find(t => t.id === draggedTaskId);
          if (!draggedTask) return currentTasks;
          const draggedIndex = newTasks.indexOf(draggedTask);
          newTasks.splice(draggedIndex, 1);
          const adjustedDropIndex = dropIndex > draggedIndex ? dropIndex - 1 : dropIndex;
          newTasks.splice(adjustedDropIndex, 0, draggedTask);
          return newTasks;
      });
  };

  useEffect(() => {
    if (!isProgressLineCalendarOpen) return;
    const handleClickOutside = (event: MouseEvent) => {
        if (!(event.target as HTMLElement).closest('[data-calendar-popover]') && !(event.target as HTMLElement).closest('[data-calendar-toggle]')) {
             setIsProgressLineCalendarOpen(false);
        }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
        document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isProgressLineCalendarOpen]);

  const handleExport = () => {
    const dataToSave = {
        projectName, projectStart, projectEnd, creationDate, creatorName, tasks,
        settings: { holidays: Array.from(holidays), columnVisibility, baseColor, progressColor, textColor, rowHeight, progressLineColor }
    };
    const jsonString = JSON.stringify(dataToSave, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    const today = new Date();
    const formattedDate = `${today.getFullYear()}-${(today.getMonth() + 1).toString().padStart(2, '0')}-${today.getDate().toString().padStart(2, '0')}`;
    a.download = `Schedule_${formattedDate}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleImportClick = () => importFileRef.current?.click();

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0];
      if (!file) return;
      const reader = new FileReader();
      reader.onload = (e) => {
          try {
              const text = e.target?.result;
              if (typeof text !== 'string') throw new Error("File content is not a string");
              const data = JSON.parse(text);
              if (!data.projectName || !data.projectStart || !data.projectEnd || !Array.isArray(data.tasks)) throw new Error("Invalid file format");

              setProjectName(data.projectName);
              setProjectStart(data.projectStart);
              setProjectEnd(data.projectEnd);
              if (data.creationDate) setCreationDate(data.creationDate);
              if (data.creatorName) setCreatorName(data.creatorName);
              setTasks(data.tasks);

              if (data.settings) {
                if (data.settings.holidays) setHolidays(new Set(data.settings.holidays));
                if (data.settings.columnVisibility) setColumnVisibility(prev => ({...prev, ...data.settings.columnVisibility}));
                if (data.settings.baseColor) setBaseColor(data.settings.baseColor);
                if (data.settings.progressColor) setProgressColor(data.settings.progressColor);
                if (data.settings.textColor) setTextColor(data.settings.textColor);
                if (data.settings.rowHeight) setRowHeight(data.settings.rowHeight);
                if (data.settings.progressLineColor) setProgressLineColor(data.settings.progressLineColor);
              }
          } catch (error) {
              console.error("Failed to import file:", error);
              alert(t('importError'));
          }
      };
      reader.readAsText(file);
      event.target.value = '';
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 font-sans">
      {isSettingsModalOpen && (
        <SettingsDialog
          onClose={() => setIsSettingsModalOpen(false)}
          holidays={holidays}
          setHolidays={setHolidays}
          columnVisibility={columnVisibility}
          setColumnVisibility={setColumnVisibility}
          baseColor={baseColor}
          setBaseColor={setBaseColor}
          progressColor={progressColor}
          setProgressColor={setProgressColor}
          textColor={textColor}
          setTextColor={setTextColor}
          progressLineColor={progressLineColor}
          setProgressLineColor={setProgressLineColor}
          rowHeight={rowHeight}
          setRowHeight={setRowHeight}
          onResetColors={handleResetColors}
        />
      )}
      {confirmModalState.isOpen && (
        <ConfirmDialog
          title={confirmModalState.title}
          message={confirmModalState.message}
          onConfirm={confirmModalState.onConfirm}
          onClose={() => setConfirmModalState({ isOpen: false, title: '', message: '', onConfirm: () => {} })}
          confirmText={t('delete')}
          cancelText={t('cancel')}
        />
      )}
      <header className="bg-white shadow-md sticky top-0 z-40">
        <div className="w-full px-4 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-slate-700 tracking-tight">{t('ganttChartMaker')}</h1>
            <p className="text-slate-500 mt-1">{t('appDescription')}</p>
          </div>
          <div className="flex items-center space-x-2 sm:space-x-4">
            <div className="flex items-center justify-end space-x-2 p-1 sm:p-2 rounded-lg bg-slate-100 border border-slate-200">
                <button onClick={() => setIsSettingsModalOpen(true)} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('settings')}>
                    <SettingsIcon className="w-5 h-5" />
                </button>
                <div className="h-6 border-l border-gray-300"></div>
                <div className="relative">
                    <button data-calendar-toggle onClick={() => setIsProgressLineCalendarOpen(prev => !prev)} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 transition-colors" title={t('progressLine')}>
                        <ProgressLineIcon className="w-5 h-5" />
                    </button>
                    {isProgressLineCalendarOpen && (
                         <DatePicker
                            initialDate={progressLineDate || undefined}
                            onSelectDate={(date) => { setProgressLineDate(date); setIsProgressLineCalendarOpen(false); }}
                            onClose={() => setIsProgressLineCalendarOpen(false)}
                            minDate={projectStart}
                            maxDate={projectEnd}
                        />
                    )}
                </div>
                {progressLineDate && (
                    <button onClick={() => setProgressLineDate(null)} className="p-1 rounded-full text-red-500 bg-red-100 hover:bg-red-200 transition-colors" title={t('clearProgressLine')}>
                        <XIcon className="w-4 h-4" />
                    </button>
                )}
                <div className="h-6 border-l border-gray-300"></div>
                <button onClick={handleZoomOut} disabled={zoomIndex === 0} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors" title={t('zoomOut')}>
                    <ZoomOutIcon className="w-5 h-5" />
                </button>
                <button onClick={handleZoomIn} disabled={zoomIndex === ZOOM_LEVELS.length - 1} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors" title={t('zoomIn')}>
                    <ZoomInIcon className="w-5 h-5" />
                </button>
            </div>
            <div className="flex items-center justify-end space-x-2 p-1 sm:p-2 rounded-lg bg-slate-100 border border-slate-200">
                <button onClick={handleImportClick} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('importData')}>
                    <UploadIcon className="w-5 h-5" />
                </button>
                <input type="file" ref={importFileRef} onChange={handleFileChange} accept=".json, .gantt.json" className="hidden" />
                <div className="h-6 border-l border-gray-300"></div>
                <button onClick={handleExport} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('exportData')}>
                    <DownloadIcon className="w-5 h-5" />
                </button>
            </div>
            <button onClick={toggleLanguage} className="px-4 py-2 border border-slate-300 rounded-md text-sm font-medium text-slate-600 hover:bg-slate-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors">
              {language === 'ja' ? 'English' : '日本語'}
            </button>
          </div>
        </div>
      </header>
      <main className="w-full p-2">
        <Timeline 
          projectName={projectName}
          setProjectName={setProjectName}
          projectStart={projectStart}
          setProjectStart={setProjectStart}
          projectEnd={projectEnd}
          setProjectEnd={setProjectEnd}
          creationDate={creationDate}
          setCreationDate={setCreationDate}
          creatorName={creatorName}
          setCreatorName={setCreatorName}
          tasks={tasks}
          holidays={holidays}
          columnVisibility={columnVisibility}
          zoomIndex={zoomIndex}
          progressLineDate={progressLineDate}
          baseColor={baseColor}
          progressColor={progressColor}
          textColor={textColor}
          progressLineColor={progressLineColor}
          rowHeight={rowHeight}
          onDeleteAllTasks={handleDeleteAllTasks}
          onAddTask={handleAddTask}
          onDeleteTask={handleDeleteTask}
          onDuplicateTask={handleDuplicateTask}
          onTaskChange={handleTaskChange}
          onDurationChange={handleDurationChange}
          onProgressChange={handleProgressChange}
          onManHoursChange={handleManHoursChange}
          onTaskDateSet={handleTaskDateSet}
          onTaskDragUpdate={handleTaskDragUpdate}
          onTaskReorder={handleTaskReorder}
        />
      </main>
    </div>
  );
};

export default Main;

import React, { useState, useMemo, useCallback, useEffect, useRef } from 'react';
import type { Task } from '../types.ts';
import { PlusIcon, TrashIcon, CalendarIcon, GripVerticalIcon, DuplicateIcon } from './Icons.tsx';
import DatePicker from './DatePicker.tsx';
import { useLanguage } from '../contexts/LocaleContext.tsx';
import { translations, TranslationKey } from '../lib/locales.ts';
import { parseUTCDateString, formatDateUTC, addDaysUTC, getDatesInRange, calculateWorkingDays } from '../lib/utils.ts';

const ZOOM_LEVELS = [8, 12, 18, 24, 40, 64];

type DragActionType = 'move' | 'resize-start' | 'resize-end';

const getReactEventCoords = (e: React.MouseEvent | React.TouchEvent) => {
    if ('touches' in e) {
        return { clientX: e.touches[0].clientX, clientY: e.touches[0].clientY };
    }
    return { clientX: e.clientX, clientY: e.clientY };
};

const getNativeEventCoords = (e: MouseEvent | TouchEvent) => {
    if (e.type.startsWith('touch')) {
        const touch = (e as TouchEvent).touches[0] || (e as TouchEvent).changedTouches[0];
        if (touch) {
            return { clientX: touch.clientX, clientY: touch.clientY };
        }
    }
    const mouseEvent = e as MouseEvent;
    return { clientX: mouseEvent.clientX, clientY: mouseEvent.clientY };
};

interface TimelineProps {
  projectName: string;
  setProjectName: React.Dispatch<React.SetStateAction<string>>;
  projectStart: string;
  setProjectStart: React.Dispatch<React.SetStateAction<string>>;
  projectEnd: string;
  setProjectEnd: React.Dispatch<React.SetStateAction<string>>;
  creationDate: string;
  setCreationDate: React.Dispatch<React.SetStateAction<string>>;
  creatorName: string;
  setCreatorName: React.Dispatch<React.SetStateAction<string>>;
  tasks: Task[];
  holidays: Set<number>;
  columnVisibility: {
    assignee: boolean;
    startDate: boolean;
    endDate: boolean;
    duration: boolean;
    progress: boolean;
    manHours: boolean;
  };
  zoomIndex: number;
  progressLineDate: string | null;
  baseColor: string;
  progressColor: string;
  textColor: string;
  progressLineColor: string;
  rowHeight: number;
  onDeleteAllTasks: () => void;
  onAddTask: () => void;
  onDeleteTask: (id: string) => void;
  onDuplicateTask: (id: string) => void;
  onTaskChange: (id: string, field: keyof Task, value: string | number | undefined) => void;
  onDurationChange: (task: Task, newDurationStr: string) => void;
  onProgressChange: (taskId: string, newProgressStr: string) => void;
  onManHoursChange: (taskId: string, newManHoursStr: string) => void;
  onTaskDateSet: (taskId: string, startDate: string, endDate: string) => void;
  onTaskDragUpdate: (taskId: string, actionType: DragActionType, initialStartDate: Date, initialEndDate: Date, dayOffset: number) => void;
  onTaskReorder: (draggedTaskId: string, dropIndex: number) => void;
}

const Timeline: React.FC<TimelineProps> = ({
  projectName, setProjectName, projectStart, setProjectStart, projectEnd, setProjectEnd, creationDate, setCreationDate, creatorName, setCreatorName,
  tasks, holidays, columnVisibility, zoomIndex, progressLineDate, baseColor, progressColor, textColor, progressLineColor, rowHeight,
  onDeleteAllTasks, onAddTask, onDeleteTask, onDuplicateTask, onTaskChange, onDurationChange, onProgressChange, onManHoursChange, onTaskDateSet, onTaskDragUpdate, onTaskReorder,
}) => {
  const { language } = useLanguage();
  const t = useCallback((key: TranslationKey) => translations[key][language], [language]);

  const [activeCalendar, setActiveCalendar] = useState<{ type: string; taskId?: string, position: 'top' | 'bottom' } | null>(null);

  const timelineHeaderRef = useRef<HTMLDivElement>(null);
  const taskRowRefs = useRef<{ [key: string]: HTMLDivElement | null }>({});
  const taskDetailsHeaderRef = useRef<HTMLDivElement>(null);
  const ganttGridRef = useRef<HTMLDivElement>(null);

  const [dragPreview, setDragPreview] = useState<{ taskId: string; start: string; end: string; } | null>(null);
  const [tooltip, setTooltip] = useState<{ visible: boolean; x: number; y: number; content: string } | null>(null);

  const [dragAction, setDragAction] = useState<{ type: DragActionType; taskId: string; initialX: number; initialStartDate: Date; initialEndDate: Date; } | null>(null);
  const [reorderState, setReorderState] = useState<{ draggedTaskId: string; dropIndex: number | null } | null>(null);
  const [totalGridWidth, setTotalGridWidth] = useState(0);

  const handleInputFocus = (e: React.FocusEvent<HTMLInputElement>) => { e.target.select(); };

  useEffect(() => {
    const gridElement = ganttGridRef.current;
    if (gridElement) {
        const observer = new ResizeObserver((entries) => {
            if (entries[0]) { setTotalGridWidth(entries[0].target.scrollWidth); }
        });
        observer.observe(gridElement);
        return () => { if (gridElement) observer.unobserve(gridElement); };
    }
  }, []);

  const dayWidth = useMemo(() => ZOOM_LEVELS[zoomIndex], [zoomIndex]);

  const openCalendar = useCallback((e: React.MouseEvent, type: string, taskId?: string) => {
    if (type === 'taskStartDate' || type === 'taskEndDate') {
      setActiveCalendar(prev => (prev?.type === type && prev?.taskId === taskId ? null : { type, taskId, position: 'bottom' }));
      return;
    }
    const button = e.currentTarget;
    const buttonRect = button.getBoundingClientRect();
    const viewportHeight = window.innerHeight;
    const spaceBelow = viewportHeight - buttonRect.bottom;
    const spaceAbove = buttonRect.top;
    const calendarHeight = 320; 
    let position: 'top' | 'bottom' = 'bottom';
    if (spaceBelow >= calendarHeight) position = 'bottom';
    else if (spaceAbove >= calendarHeight) position = 'top';
    else if (spaceAbove > spaceBelow) position = 'top';

    setActiveCalendar(prev => (prev?.type === type && prev?.taskId === taskId ? null : { type, taskId, position }));
  }, []);

  useEffect(() => {
    if (!activeCalendar) return;
    const handleClickOutside = (event: MouseEvent) => {
        if (!(event.target as HTMLElement).closest('[data-calendar-popover]') && !(event.target as HTMLElement).closest('[data-calendar-toggle]')) {
             setActiveCalendar(null);
        }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [activeCalendar]);

  const dateArray = useMemo(() => {
    const start = parseUTCDateString(projectStart);
    const end = parseUTCDateString(projectEnd);
    if (!start || !end || start > end) return [];
    return getDatesInRange(start, end);
  }, [projectStart, projectEnd]);

  const getTaskDuration = useCallback((startDateStr: string, endDateStr: string): number => {
    const start = parseUTCDateString(startDateStr);
    const end = parseUTCDateString(endDateStr);
    if (!start || !end) return 0;
    return calculateWorkingDays(start, end, holidays);
  }, [holidays]);

  const getGridPosition = (start: Date, end: Date) => {
    if (dateArray.length === 0 || end < start) return { gridColumn: '1 / span 1', opacity: 0 };
    const projectStartDate = dateArray[0];
    const projectEndDate = dateArray[dateArray.length - 1];
    const renderStart = new Date(Math.max(start.getTime(), projectStartDate.getTime()));
    const renderEnd = new Date(Math.min(end.getTime(), projectEndDate.getTime()));
    if (renderEnd < renderStart) return { gridColumn: '1 / span 1', opacity: 0 };
    const startIndex = dateArray.findIndex(d => d.getTime() === renderStart.getTime());
    const endIndex = dateArray.findIndex(d => d.getTime() === renderEnd.getTime());
    if (startIndex === -1 || endIndex === -1) return { gridColumn: '1 / span 1', opacity: 0 };
    return { gridColumn: `${startIndex + 1} / ${endIndex + 2}` };
  };

  const getTaskSegments = useCallback((task: Task): {startDate: Date, endDate: Date}[] => {
    const start = parseUTCDateString(task.startDate);
    const end = parseUTCDateString(task.endDate);
    if (!start || !end || end < start) return [];
    const segments: {startDate: Date, endDate: Date}[] = [];
    let segmentStart: Date | null = null;
    const current = new Date(start.getTime());
    while(current.getTime() <= end.getTime()){
      const isHoliday = holidays.has(current.getUTCDay());
      if(!isHoliday && segmentStart === null){
        segmentStart = new Date(current.getTime());
      } else if(isHoliday && segmentStart !== null){
        const segmentEnd = new Date(current.getTime());
        segmentEnd.setUTCDate(segmentEnd.getUTCDate() - 1);
        segments.push({startDate: segmentStart, endDate: segmentEnd});
        segmentStart = null;
      }
      current.setUTCDate(current.getUTCDate() + 1);
    }
    if (segmentStart !== null) segments.push({startDate: segmentStart, endDate: end});
    return segments;
  }, [holidays]);

  const monthHeaders = useMemo(() => {
    const months: { formatted: string; days: number }[] = [];
    if (dateArray.length === 0) return months;
    let currentMonth = -1;
    let currentYear = -1;
    dateArray.forEach(date => {
        const month = date.getUTCMonth();
        const year = date.getUTCFullYear();
        if (month !== currentMonth || year !== currentYear) {
            currentMonth = month;
            currentYear = year;
            const formattedString = date.toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US', { year: 'numeric', month: 'long', timeZone: 'UTC' });
            months.push({ formatted: formattedString, days: 0 });
        }
        months[months.length - 1].days++;
    });
    return months;
  }, [dateArray, language]);

  const dateHeaders = useMemo(() => {
    if (dateArray.length === 0) return [];
    let interval = 1;
    if (dayWidth < 24) interval = 2;
    if (dayWidth < 18) interval = 3;
    if (dayWidth < 12) interval = 7;
    const headers = [];
    for (let i = 0; i < dateArray.length; i += interval) {
      const date = dateArray[i];
      const remainingDays = dateArray.length - i;
      const span = Math.min(interval, remainingDays);
      const lastDayInSpan = dateArray[i + span - 1];
      const dayAfterLast = addDaysUTC(lastDayInSpan, 1);
      const isLastDayOfMonth = dayAfterLast.getUTCDate() === 1;
      headers.push({ date, span, isLastDayOfMonth });
    }
    return headers;
  }, [dateArray, dayWidth]);

  const getDateFromX = useCallback((x: number) => {
    if (!ganttGridRef.current || !taskDetailsHeaderRef.current || dateArray.length === 0) return null;
    const gridRect = ganttGridRef.current.getBoundingClientRect();
    const detailsWidth = taskDetailsHeaderRef.current.offsetWidth;
    const timelineStartLeft = gridRect.left + detailsWidth;
    const relativeX = x - timelineStartLeft;
    if (dayWidth <= 0 || relativeX < 0) return null;
    const dateIndex = Math.floor(relativeX / dayWidth);
    if (dateIndex >= 0 && dateIndex < dateArray.length) {
        return dateArray[dateIndex];
    }
    return null;
  }, [dateArray, dayWidth]);

 const handlePointerDownForCreate = useCallback((e: React.MouseEvent<HTMLDivElement> | React.TouchEvent<HTMLDivElement>) => {
      if (e.type.startsWith('touch')) e.preventDefault();
      const { clientX } = getReactEventCoords(e);
      const startDate = getDateFromX(clientX);
      if (startDate) {
          if (holidays.has(startDate.getUTCDay())) return;
          const taskId = (e.currentTarget as HTMLElement).dataset.taskRowId;
          if (taskId) {
            const startDateStr = formatDateUTC(startDate);
            setDragPreview({ taskId, start: startDateStr, end: startDateStr });
          }
      }
      setTooltip(null);
  }, [getDateFromX, holidays]);

  const handlePointerMove = useCallback((e: React.MouseEvent<HTMLDivElement> | React.TouchEvent<HTMLDivElement>) => {
    const timelineRowCell = e.currentTarget;
    const { clientX, clientY } = getReactEventCoords(e);
    if(dragPreview) {
        if (e.type === 'touchmove') e.preventDefault();
        const currentDate = getDateFromX(clientX);
        if (currentDate) {
            setDragPreview(prev => (prev ? { ...prev, end: formatDateUTC(currentDate) } : null));
        }
        return;
    }
    if (e.type.startsWith('touch') || dragAction || reorderState) {
        setTooltip(null);
        return;
    }
    const taskId = timelineRowCell.dataset.taskRowId;
    const task = tasks.find(t => t.id === taskId);
    if (task && !task.startDate && !task.endDate) {
        const dateUnderCursor = getDateFromX(clientX);
        const isHolidayUnderCursor = dateUnderCursor ? holidays.has(dateUnderCursor.getUTCDay()) : true;
        if (isHolidayUnderCursor) {
            timelineRowCell.style.cursor = 'not-allowed';
            setTooltip(null);
        } else {
            timelineRowCell.style.cursor = 'crosshair';
            setTooltip({ visible: true, x: clientX, y: clientY, content: t('dragToSetDuration') });
        }
    } else {
        timelineRowCell.style.cursor = 'default';
        setTooltip(null);
    }
  }, [dragPreview, dragAction, reorderState, tasks, getDateFromX, holidays, t]);

  const handleMouseLeave = useCallback((e: React.MouseEvent<HTMLDivElement>) => {
    e.currentTarget.style.cursor = 'default';
    setTooltip(null);
  }, []);

  useEffect(() => {
    if (!dragPreview) return;
    const handlePointerUpGlobal = () => {
        if (dragPreview) {
            onTaskDateSet(dragPreview.taskId, dragPreview.start, dragPreview.end);
            setDragPreview(null);
        }
    };
    window.addEventListener('mouseup', handlePointerUpGlobal);
    window.addEventListener('touchend', handlePointerUpGlobal);
    return () => {
        window.removeEventListener('mouseup', handlePointerUpGlobal);
        window.removeEventListener('touchend', handlePointerUpGlobal);
    };
  }, [dragPreview, onTaskDateSet]);

  const handleDragStart = useCallback((e: React.MouseEvent | React.TouchEvent, task: Task, type: DragActionType) => {
    if (e.type.startsWith('touch')) e.preventDefault();
    e.stopPropagation();
    const start = parseUTCDateString(task.startDate);
    const end = parseUTCDateString(task.endDate);
    if (!start || !end) return;
    const { clientX } = getReactEventCoords(e);
    setDragAction({ type, taskId: task.id, initialX: clientX, initialStartDate: start, initialEndDate: end });
  }, []);

  const handleDragMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (e.type === 'touchmove') e.preventDefault();
    if (!dragAction || dayWidth <= 0) return;
    const coords = getNativeEventCoords(e);
    if (!coords) return;
    const { clientX } = coords;
    const deltaX = clientX - dragAction.initialX;
    const dayOffset = Math.round(deltaX / dayWidth);
    onTaskDragUpdate(dragAction.taskId, dragAction.type, dragAction.initialStartDate, dragAction.initialEndDate, dayOffset);
    const task = tasks.find(t => t.id === dragAction.taskId);
    if (task) {
        const newStart = parseUTCDateString(task.startDate);
        const newEnd = parseUTCDateString(task.endDate);
        if (newStart && newEnd && newStart > newEnd) {
            if (dragAction.type === 'resize-start') setDragAction(prev => (prev ? { ...prev, type: 'resize-end' } : null));
            else if (dragAction.type === 'resize-end') setDragAction(prev => (prev ? { ...prev, type: 'resize-start' } : null));
        }
    }
  }, [dragAction, dayWidth, onTaskDragUpdate, tasks]);

  const handleDragEnd = useCallback(() => setDragAction(null), []);

  useEffect(() => {
    const getCursor = (type: DragActionType) => {
      switch (type) {
        case 'move': return 'grabbing';
        case 'resize-start': case 'resize-end': return 'ew-resize';
        default: return 'auto';
      }
    };
    if (dragAction) {
        document.body.style.cursor = getCursor(dragAction.type);
        document.body.style.userSelect = 'none';
        window.addEventListener('mousemove', handleDragMove);
        window.addEventListener('touchmove', handleDragMove, { passive: false });
        window.addEventListener('mouseup', handleDragEnd);
        window.addEventListener('touchend', handleDragEnd);
    }
    return () => {
        window.removeEventListener('mousemove', handleDragMove);
        window.removeEventListener('touchmove', handleDragMove);
        window.removeEventListener('mouseup', handleDragEnd);
        window.removeEventListener('touchend', handleDragEnd);
        if (document.body) { document.body.style.cursor = 'auto'; document.body.style.userSelect = 'auto'; }
    };
  }, [dragAction, handleDragMove, handleDragEnd]);

  const handleReorderStart = useCallback((e: React.MouseEvent | React.TouchEvent, taskId: string) => {
    e.preventDefault(); e.stopPropagation();
    setReorderState({ draggedTaskId: taskId, dropIndex: null });
  }, []);

  const handleReorderMove = useCallback((e: MouseEvent | TouchEvent) => {
    if (e.type === 'touchmove') e.preventDefault();
    if (!reorderState) return;
    const coords = getNativeEventCoords(e);
    if (!coords) return;
    const { clientY } = coords;
    const positions = tasks.map(task => {
        const el = taskRowRefs.current[task.id];
        if (!el) return { id: task.id, top: -1, bottom: -1, mid: -1 };
        const rect = el.getBoundingClientRect();
        return { id: task.id, top: rect.top, bottom: rect.bottom, mid: rect.top + rect.height / 2 };
    }).filter(p => p.top !== -1);
    const target = positions.find(p => clientY >= p.top && clientY <= p.bottom);
    let newDropIndex: number | null = null;
    if (target) {
        const targetIndex = tasks.findIndex(t => t.id === target.id);
        newDropIndex = clientY < target.mid ? targetIndex : targetIndex + 1;
    } else {
        const firstTaskRect = taskRowRefs.current[tasks[0]?.id]?.getBoundingClientRect();
        if (firstTaskRect && clientY < firstTaskRect.top) newDropIndex = 0;
        else {
            const lastTaskRect = taskRowRefs.current[tasks[tasks.length-1]?.id]?.getBoundingClientRect();
            if(lastTaskRect && clientY > lastTaskRect.bottom) newDropIndex = tasks.length;
        }
    }
    setReorderState(prev => (prev ? { ...prev, dropIndex: newDropIndex } : null));
  }, [reorderState, tasks]);

  const handleReorderEnd = useCallback(() => {
    if (reorderState && typeof reorderState.dropIndex === 'number') onTaskReorder(reorderState.draggedTaskId, reorderState.dropIndex);
    setReorderState(null);
  }, [reorderState, onTaskReorder]);

  useEffect(() => {
    if (reorderState?.draggedTaskId) {
        document.body.style.cursor = 'grabbing';
        document.body.style.userSelect = 'none';
        window.addEventListener('mousemove', handleReorderMove);
        window.addEventListener('touchmove', handleReorderMove, { passive: false });
        window.addEventListener('mouseup', handleReorderEnd);
        window.addEventListener('touchend', handleReorderEnd);
    }
    return () => {
        window.removeEventListener('mousemove', handleReorderMove);
        window.removeEventListener('touchmove', handleReorderMove);
        window.removeEventListener('mouseup', handleReorderEnd);
        window.removeEventListener('touchend', handleReorderEnd);
        if (document.body) { document.body.style.cursor = 'auto'; document.body.style.userSelect = 'auto'; }
    };
  }, [reorderState, handleReorderMove, handleReorderEnd]);

  const dropIndex = reorderState?.dropIndex;

  const progressLinePath = useMemo(() => {
    if (!progressLineDate || dateArray.length === 0 || !taskDetailsHeaderRef.current || !timelineHeaderRef.current) return null;
    const headerHeight = timelineHeaderRef.current.offsetHeight;
    const taskDetailsWidth = taskDetailsHeaderRef.current.offsetWidth || 0;
    const baselineDate = parseUTCDateString(progressLineDate);
    if (!baselineDate) return null;
    const baselineDateIndex = dateArray.findIndex(d => d.getTime() === baselineDate.getTime());
    if (baselineDateIndex === -1) return null;
    const baselineX = (baselineDateIndex + 1) * dayWidth;
    const taskProgressXs = tasks.map(task => {
        let currentTaskX = baselineX;
        const startDate = parseUTCDateString(task.startDate);
        const endDate = parseUTCDateString(task.endDate);
        if (startDate && endDate) {
            const isCompletedEarly = endDate.getTime() < baselineDate.getTime() && task.progress === 100;
            const isNotYetStarted = startDate.getTime() > baselineDate.getTime() && task.progress === 0;
            if (!isCompletedEarly && !isNotYetStarted) {
                const totalWorkDays = calculateWorkingDays(startDate, endDate, holidays);
                if (totalWorkDays > 0) {
                    let completedWorkDays = totalWorkDays * (task.progress / 100);
                    let currentPosDate = new Date(startDate.getTime());
                    let pixelOffset = 0;
                    while(completedWorkDays > 0 && currentPosDate.getTime() <= endDate.getTime()) {
                        if(!holidays.has(currentPosDate.getUTCDay())){
                           const consumption = Math.min(1, completedWorkDays);
                           pixelOffset += consumption * dayWidth;
                           completedWorkDays -= consumption;
                        } else pixelOffset += dayWidth;
                        if (completedWorkDays > 0) currentPosDate.setUTCDate(currentPosDate.getUTCDate() + 1);
                    }
                    const startIndex = dateArray.findIndex(d => d.getTime() === startDate.getTime());
                    if(startIndex !== -1) currentTaskX = (startIndex * dayWidth) + pixelOffset;
                }
            }
        }
        return taskDetailsWidth + currentTaskX;
    });
    const taskBarHeight = 32;
    const spaceAboveBelowBar = (rowHeight - taskBarHeight) / 2;
    const finalBaselineX = taskDetailsWidth + baselineX;
    let path = `M ${finalBaselineX} 0 L ${finalBaselineX} ${headerHeight}`;
    if (tasks.length > 0 && taskProgressXs.length > 0) {
        tasks.forEach((_, i) => {
            const progressX = taskProgressXs[i];
            const rowTopY = headerHeight + i * rowHeight;
            const barTopY = rowTopY + spaceAboveBelowBar;
            const barCenterY = rowTopY + rowHeight / 2;
            const barBottomY = rowTopY + rowHeight - spaceAboveBelowBar;
            const rowBottomY = rowTopY + rowHeight;
            path += ` L ${finalBaselineX} ${barTopY} L ${progressX} ${barCenterY} L ${finalBaselineX} ${barBottomY} L ${finalBaselineX} ${rowBottomY}`;
        });
    }
    return path;
  }, [progressLineDate, tasks, dateArray, dayWidth, holidays, rowHeight]);

  const totalContentHeight = (timelineHeaderRef.current?.offsetHeight || 0) + tasks.length * rowHeight;
  const totalManHours = useMemo(() => tasks.reduce((sum, task) => sum + (task.manHours || 0), 0), [tasks]);

  return (
    <div className="bg-white rounded-lg shadow-lg flex flex-col max-h-[calc(100vh-16rem)] sm:max-h-[calc(100vh-14rem)]">
      {tooltip?.visible && (
        <div className="fixed bg-gray-800 text-white text-xs rounded py-1 px-2 pointer-events-none shadow-lg" style={{ top: tooltip.y + 20, left: tooltip.x + 15, zIndex: 1000 }}>
            {tooltip.content}
        </div>
      )}
      <div className="p-4 sm:p-6 flex-shrink-0 border-b">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-x-6 gap-y-4 items-end">
          <div className="md:col-span-2 flex flex-wrap items-end gap-x-4 gap-y-2">
            <div className="flex-grow min-w-[300px]">
              <label className="block text-sm font-medium text-gray-700 mb-1">{t('projectNameLabel')}</label>
              <input type="text" value={projectName} onChange={e => setProjectName(e.target.value)} onFocus={handleInputFocus} placeholder={t('projectNamePlaceholder')} className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm text-base font-semibold focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" />
            </div>
            <div className="relative w-36 flex-shrink-0">
              <label className="block text-sm font-medium text-gray-700 mb-1">{t('creationDate')}</label>
              <div className="relative">
                <input type="text" value={creationDate} onChange={e => setCreationDate(e.target.value)} onFocus={handleInputFocus} placeholder={t('dateFormatPlaceholder')} className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 pr-10" />
                <button data-calendar-toggle onClick={(e) => openCalendar(e, 'creationDate')} className="absolute inset-y-0 right-0 flex items-center pr-3 text-gray-500 hover:text-indigo-600 transition-colors" aria-label={t('openCalendar')}>
                  <CalendarIcon className="w-5 h-5" />
                </button>
              </div>
              {activeCalendar?.type === 'creationDate' && <DatePicker initialDate={creationDate} onSelectDate={(date) => { setCreationDate(date); setActiveCalendar(null); }} onClose={() => setActiveCalendar(null)} showToday={true} position={activeCalendar.position} />}
            </div>
            <div className="w-36 flex-shrink-0">
              <label className="block text-sm font-medium text-gray-700 mb-1">{t('creator')}</label>
              <input type="text" value={creatorName} onChange={e => setCreatorName(e.target.value)} onFocus={handleInputFocus} placeholder={t('creator')} className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" />
            </div>
          </div>
          <div className="relative">
            <label className="block text-sm font-medium text-gray-700 mb-1">{t('projectStartDate')}</label>
            <div className="relative">
              <input type="text" value={projectStart} onChange={e => setProjectStart(e.target.value)} placeholder={t('dateFormatPlaceholder')} className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 pr-10" />
              <button data-calendar-toggle onClick={(e) => openCalendar(e, 'projectStart')} className="absolute inset-y-0 right-0 flex items-center pr-3 text-gray-500 hover:text-indigo-600 transition-colors" aria-label={t('openCalendar')}>
                <CalendarIcon className="w-5 h-5" />
              </button>
            </div>
            {activeCalendar?.type === 'projectStart' && <DatePicker initialDate={projectStart} onSelectDate={(date) => { setProjectStart(date); setActiveCalendar(null); }} onClose={() => setActiveCalendar(null)} position={activeCalendar.position} />}
          </div>
          <div className="flex items-end gap-2">
            <div className="relative flex-grow">
              <label className="block text-sm font-medium text-gray-700 mb-1">{t('projectEndDate')}</label>
              <div className="relative">
                <input type="text" value={projectEnd} onChange={e => setProjectEnd(e.target.value)} placeholder={t('dateFormatPlaceholder')} className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 pr-10" />
                <button data-calendar-toggle onClick={(e) => openCalendar(e, 'projectEnd')} className="absolute inset-y-0 right-0 flex items-center pr-3 text-gray-500 hover:text-indigo-600 transition-colors" aria-label={t('openCalendar')}>
                  <CalendarIcon className="w-5 h-5" />
                </button>
              </div>
              {activeCalendar?.type === 'projectEnd' && <DatePicker initialDate={projectEnd} onSelectDate={(date) => { setProjectEnd(date); setActiveCalendar(null); }} onClose={() => setActiveCalendar(null)} position={activeCalendar.position} />}
            </div>
            <button onClick={onAddTask} title={t('addTask')} className="flex-shrink-0 h-[42px] w-[42px] bg-indigo-600 text-white rounded-md flex items-center justify-center shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors">
                <PlusIcon className="w-6 h-6" />
            </button>
          </div>
        </div>
      </div>

      <div className="relative overflow-auto flex-grow">
        <div ref={ganttGridRef} className="gantt-grid min-w-[1200px] grid relative" style={{ gridTemplateColumns: `auto 1fr`, gridTemplateRows: `auto repeat(${tasks.length}, ${rowHeight}px)` }}>
          <div ref={taskDetailsHeaderRef} className="sticky top-0 left-0 bg-slate-100 p-2 border-b border-r border-gray-200 font-semibold text-slate-600 text-sm flex items-center z-30">
            <div className="flex items-center w-full gap-1">
              <div className="w-6 flex-shrink-0" />
              <div className="flex-grow min-w-0 p-1">{t('taskDetails')}</div>
              {columnVisibility.assignee && <div className="w-24 flex-shrink-0 p-1 text-xs text-center">{t('assignee')}</div>}
              {columnVisibility.startDate && <div className="w-24 flex-shrink-0 p-1 text-xs text-center">{t('startDate')}</div>}
              {columnVisibility.endDate && <div className="w-24 flex-shrink-0 p-1 text-xs text-center">{t('endDate')}</div>}
              {columnVisibility.duration && <div className="w-16 flex-shrink-0 p-1 text-xs text-center">{t('duration')}</div>}
              {columnVisibility.progress && <div className="w-20 flex-shrink-0 p-1 text-xs text-center">{t('progress')}</div>}
              {columnVisibility.manHours && (
                <div className="w-20 flex-shrink-0 p-1 text-xs text-center">
                  <div className="flex flex-col items-center justify-center -space-y-1">
                    <span>{t('manHours')}</span>
                    <span className="text-[10px] text-slate-500 font-normal">({t('total')}: {totalManHours.toLocaleString(undefined, { maximumFractionDigits: 1 })})</span>
                  </div>
                </div>
              )}
              <div className="w-16 flex-shrink-0 flex items-center justify-end">
                 <button onClick={onDeleteAllTasks} className="text-gray-400 hover:text-red-500 p-1 rounded-full transition-colors disabled:opacity-50 disabled:cursor-not-allowed" title={t('deleteAllTasks')} disabled={tasks.length === 0}><TrashIcon className="w-5 h-5"/></button>
              </div>
            </div>
          </div>
          <div className="sticky top-0 z-10 bg-slate-50 border-b border-gray-200" style={{ gridColumn: `2 / span 1` }} ref={timelineHeaderRef}>
            <div className="grid" style={{ gridTemplateColumns: `repeat(${dateArray.length}, ${dayWidth}px)` }}>
              {monthHeaders.map((month, index) => (
                <div key={index} className="text-center font-semibold text-slate-600 text-sm py-1 border-r border-gray-400" style={{ gridColumn: `span ${month.days}` }}>{month.formatted}</div>
              ))}
            </div>
            <div className="grid" style={{ gridTemplateColumns: `repeat(${dateArray.length}, ${dayWidth}px)` }}>
              {dateHeaders.map(({ date, span, isLastDayOfMonth }, index) => {
                const dayOfWeek = date.toLocaleString(language === 'ja' ? 'ja-JP' : 'en-US', { weekday: 'short', timeZone: 'UTC' });
                const day = date.getUTCDay();
                const isHoliday = holidays.has(day);
                let dayColorClass = 'text-slate-500';
                if (day === 0) dayColorClass = 'text-red-600'; else if (day === 6) dayColorClass = 'text-blue-600';
                let borderClass = 'border-gray-200';
                if (isLastDayOfMonth) borderClass = 'border-gray-400'; else if (span > 1) borderClass = 'border-gray-300';
                return (
                  <div key={index} className={`text-xs border-r ${borderClass} flex ${span === 1 ? 'justify-center' : 'justify-start pl-1'} items-center h-full ${isHoliday ? 'bg-red-50' : 'bg-slate-50'} ${dayColorClass} overflow-hidden`} style={{ gridColumn: `span ${span}` }}>
                      <div className={`flex flex-col ${span === 1 ? 'items-center' : 'items-start'} justify-center h-full`}>
                        <span className="whitespace-nowrap leading-none">{date.getUTCDate()}</span>
                        <span className="text-[10px] leading-none whitespace-nowrap">{dayOfWeek}</span>
                      </div>
                  </div>
                );
              })}
            </div>
          </div>
          {typeof dropIndex === 'number' && reorderState && (
            <div className="h-0.5 bg-blue-500 pointer-events-none absolute w-full z-40" style={{ top: (timelineHeaderRef.current?.offsetHeight || 0) + (tasks.findIndex(t => t.id === reorderState.draggedTaskId) < dropIndex ? dropIndex -1 : dropIndex) * rowHeight - 2, left: 0 }} />
          )}
          {tasks.map((task, index) => {
            const isDragging = reorderState?.draggedTaskId === task.id;
            return (
            <React.Fragment key={task.id}>
              <div ref={(el) => { taskRowRefs.current[task.id] = el; }} className={`sticky left-0 bg-white border-b border-r border-gray-200 z-20 flex items-center p-2 gap-1 transition-opacity ${isDragging ? 'opacity-50' : ''}`} style={{ height: `${rowHeight}px`, gridRow: index + 2, gridColumn: 1, zIndex: activeCalendar?.taskId === task.id ? 41 : 20 }}>
                  <button onMouseDown={(e) => handleReorderStart(e, task.id)} onTouchStart={(e) => handleReorderStart(e, task.id)} className="p-1 text-gray-400 hover:bg-gray-200 rounded-md" title={t('reorderTask')}><GripVerticalIcon className="w-5 h-5 cursor-grab active:cursor-grabbing"/></button>
                  <input type="text" value={task.name} onChange={e => onTaskChange(task.id, 'name', e.target.value)} onFocus={handleInputFocus} placeholder={t('taskNamePlaceholder')} className="flex-grow min-w-0 p-1 border-none focus:ring-0 text-sm"/>
                  {columnVisibility.assignee && <div className="w-24 flex-shrink-0"><input type="text" value={task.assignee || ''} onChange={e => onTaskChange(task.id, 'assignee', e.target.value)} onFocus={handleInputFocus} placeholder={t('assignee')} className="w-full p-1 border-none focus:ring-0 text-xs"/></div>}
                  {columnVisibility.startDate && <div className="relative w-24 flex-shrink-0"><input type="text" value={task.startDate} onChange={e => onTaskChange(task.id, 'startDate', e.target.value)} placeholder={t('dateFormatPlaceholder')} className="w-full p-1 border-none focus:ring-0 text-xs pr-6"/><button data-calendar-toggle onClick={(e) => openCalendar(e, 'taskStartDate', task.id)} className="absolute inset-y-0 right-0 flex items-center pr-1 text-gray-400 hover:text-indigo-600" aria-label={t('openCalendar')}><CalendarIcon className="w-4 h-4"/></button>{activeCalendar?.type === 'taskStartDate' && activeCalendar?.taskId === task.id && <DatePicker initialDate={task.startDate} taskStartDate={task.startDate} taskEndDate={task.endDate} minDate={projectStart} maxDate={task.endDate || projectEnd} onSelectDate={(date) => { onTaskChange(task.id, 'startDate', date); setActiveCalendar(null); }} onClose={() => setActiveCalendar(null)} position={activeCalendar.position} />}</div>}
                  {columnVisibility.endDate && <div className="relative w-24 flex-shrink-0"><input type="text" value={task.endDate} onChange={e => onTaskChange(task.id, 'endDate', e.target.value)} placeholder={t('dateFormatPlaceholder')} className="w-full p-1 border-none focus:ring-0 text-xs pr-6"/><button data-calendar-toggle onClick={(e) => openCalendar(e, 'taskEndDate', task.id)} className="absolute inset-y-0 right-0 flex items-center pr-1 text-gray-400 hover:text-indigo-600" aria-label={t('openCalendar')}><CalendarIcon className="w-4 h-4"/></button>{activeCalendar?.type === 'taskEndDate' && activeCalendar?.taskId === task.id && <DatePicker initialDate={task.endDate} taskStartDate={task.startDate} taskEndDate={task.endDate} minDate={task.startDate || projectStart} maxDate={projectEnd} onSelectDate={(date) => { onTaskChange(task.id, 'endDate', date); setActiveCalendar(null); }} onClose={() => setActiveCalendar(null)} position={activeCalendar.position} />}</div>}
                  {columnVisibility.duration && <div className="w-16 flex-shrink-0"><input type="number" value={getTaskDuration(task.startDate, task.endDate) || ''} onChange={e => onDurationChange(task, e.target.value)} onFocus={handleInputFocus} className="w-full p-1 border-none focus:ring-0 text-xs text-center" placeholder="-" min="1"/></div>}
                  {columnVisibility.progress && <div className="w-20 flex-shrink-0 relative"><input type="number" value={task.progress} onChange={e => onProgressChange(task.id, e.target.value)} onFocus={handleInputFocus} className="w-full p-1 border-none focus:ring-0 text-xs text-center pr-4" min="0" max="100"/><span className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-gray-400">%</span></div>}
                  {columnVisibility.manHours && <div className="w-20 flex-shrink-0"><input type="number" value={task.manHours ?? ''} onChange={e => onManHoursChange(task.id, e.target.value)} onFocus={handleInputFocus} className="w-full p-1 border-none focus:ring-0 text-xs text-center" placeholder="-" min="0"/></div>}
                  <div className="w-16 flex-shrink-0 flex items-center justify-end"><button onClick={() => onDuplicateTask(task.id)} className="text-gray-400 hover:text-indigo-500 p-1 rounded-full transition-colors" title={t('duplicateTask')}><DuplicateIcon className="w-5 h-5"/></button><button onClick={() => onDeleteTask(task.id)} className="text-gray-400 hover:text-red-500 p-1 rounded-full transition-colors"><TrashIcon className="w-5 h-5"/></button></div>
              </div>
              <div className={`relative border-b transition-opacity z-0 ${isDragging ? 'opacity-50' : ''}`} style={{ gridRow: index + 2, gridColumn: `2 / span 1` }} data-task-row-id={task.id} onMouseDown={(e) => { if (!task.startDate && !task.endDate) handlePointerDownForCreate(e); }} onTouchStart={(e) => { if (!task.startDate && !task.endDate) handlePointerDownForCreate(e); }} onMouseMove={handlePointerMove} onTouchMove={handlePointerMove} onMouseLeave={handleMouseLeave}>
                  <div className="absolute inset-0 grid" style={{ gridTemplateColumns: `repeat(${dateArray.length}, ${dayWidth}px)` }}>
                      {dateArray.map((date, index) => {
                          const isHoliday = holidays.has(date.getUTCDay());
                          const nextDay = addDaysUTC(date, 1);
                          const isLastDayOfMonth = nextDay.getUTCDate() === 1;
                          let interval = 1;
                          if (dayWidth < 24) interval = 2;
                          if (dayWidth < 18) interval = 3;
                          if (dayWidth < 12) interval = 7;
                          const isGroupSeparator = interval > 1 && (index + 1) % interval === 0;
                          let borderClass = 'border-gray-100';
                          if (isLastDayOfMonth) borderClass = 'border-gray-400'; else if (isGroupSeparator) borderClass = 'border-gray-300';
                          return <div key={index} className={`h-full border-r ${borderClass} ${isHoliday ? 'bg-red-50' : ''}`}></div>;
                      })}
                  </div>
                  <div className="absolute inset-0 grid items-center" style={{ gridTemplateColumns: `repeat(${dateArray.length}, ${dayWidth}px)` }}>
                     {(() => {
                        const taskStart = parseUTCDateString(task.startDate);
                        const taskEnd = parseUTCDateString(task.endDate);
                        if (!taskStart || !taskEnd || taskEnd < taskStart) return null;
                        const totalWorkingDays = getTaskDuration(task.startDate, task.endDate);
                        if (totalWorkingDays === 0) return null;
                        let completedWorkingDays = totalWorkingDays * (task.progress / 100);
                        const lightningSvg = `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" class="w-5 h-5"><path fill-rule="evenodd" d="M11.933 3.339a.75.75 0 01.328 1.309l-6 7.5a.75.75 0 01-1.261-.548l2.25-9.75a.75.75 0 011.355-.26l2.328 1.75Z" clip-rule="evenodd" /><path d="M4.583 8.883c.31.043.614.12.906.224l-2.25 9.75a.75.75 0 01-1.355.26L.182 17.367a.75.75 0 01.26-1.025l4.14-1.453Z" /><path d="M12.75 11.25a.75.75 0 01.75-.75h4.5a.75.75 0 010 1.5h-4.5a.75.75 0 01-.75-.75Z" /></svg>`;
                        const lightningDataUrl = `url("data:image/svg+xml;base64,${btoa(lightningSvg.replace('<svg ', `<svg fill='rgba(31, 41, 55, 0.4)' `))}")`;
                        const totalDurationDays = (taskEnd.getTime() - taskStart.getTime()) / (1000 * 3600 * 24) + 1;
                        if (totalDurationDays <= 0) return null;
                        const segments = getTaskSegments(task);
                        const interactionLayerStyle: React.CSSProperties = { color: textColor };
                        if (segments.length > 0 && totalDurationDays > 0) {
                            const visualStartDate = segments[0].startDate;
                            const visualEndDate = segments[segments.length - 1].endDate;
                            const startOffsetDays = (visualStartDate.getTime() - taskStart.getTime()) / (1000 * 3600 * 24);
                            const endOffsetDays = (taskEnd.getTime() - visualEndDate.getTime()) / (1000 * 3600 * 24);
                            interactionLayerStyle.position = 'absolute'; interactionLayerStyle.top = '0'; interactionLayerStyle.bottom = '0';
                            interactionLayerStyle.left = `${(startOffsetDays / totalDurationDays) * 100}%`; interactionLayerStyle.right = `${(endOffsetDays / totalDurationDays) * 100}%`;
                        } else interactionLayerStyle.display = 'none';
                        return (
                            <div className="relative h-8" style={{ ...getGridPosition(taskStart, taskEnd) }}>
                                <div className="absolute inset-0">
                                    {segments.map((segment, segIndex) => {
                                        const segmentWorkingDays = calculateWorkingDays(segment.startDate, segment.endDate, holidays);
                                        if (segmentWorkingDays === 0) return null;
                                        const progressDaysInSegment = Math.max(0, Math.min(segmentWorkingDays, completedWorkingDays));
                                        const progressWidthPercent = (progressDaysInSegment / segmentWorkingDays) * 100;
                                        completedWorkingDays -= progressDaysInSegment;
                                        const segmentStartOffsetDays = (segment.startDate.getTime() - taskStart.getTime()) / (1000 * 3600 * 24);
                                        const segmentDurationDays = (segment.endDate.getTime() - segment.startDate.getTime()) / (1000 * 3600 * 24) + 1;
                                        const left = (segmentStartOffsetDays / totalDurationDays) * 100;
                                        const width = (segmentDurationDays / totalDurationDays) * 100;
                                        return (
                                            <div key={`segment-${segIndex}`} className="absolute top-0 h-full" style={{ left: `${left}%`, width: `${width}%` }}>
                                                <div className="w-full h-full relative" style={{ backgroundColor: baseColor }}>
                                                    <div className="absolute top-0 left-0 h-full pointer-events-none" style={{ width: `${progressWidthPercent}%`, backgroundColor: progressColor, backgroundImage: lightningDataUrl, backgroundSize: '16px' }} />
                                                </div>
                                            </div>
                                        );
                                    })}
                                </div>
                                <div className="group/bar cursor-move px-3 text-sm font-medium flex items-center" style={{ ...interactionLayerStyle, touchAction: 'none' }} onMouseDown={(e) => handleDragStart(e, task, 'move')} onTouchStart={(e) => handleDragStart(e, task, 'move')}>
                                    <span className="whitespace-nowrap">{task.name}</span>
                                    <div className="absolute left-0 top-0 h-full w-2 cursor-ew-resize" onMouseDown={(e) => handleDragStart(e, task, 'resize-start')} onTouchStart={(e) => handleDragStart(e, task, 'resize-start')} />
                                    <div className="absolute right-0 top-0 h-full w-2 cursor-ew-resize" onMouseDown={(e) => handleDragStart(e, task, 'resize-end')} onTouchStart={(e) => handleDragStart(e, task, 'resize-end')} />
                                </div>
                            </div>
                        );
                     })()}
                     {dragPreview && dragPreview.taskId === task.id && (
                        <div className="h-8 bg-indigo-300 opacity-70" style={getGridPosition(parseUTCDateString(dragPreview.start > dragPreview.end ? dragPreview.end : dragPreview.start)!, parseUTCDateString(dragPreview.start > dragPreview.end ? dragPreview.start : dragPreview.end)!)} />
                     )}
                  </div>
              </div>
            </React.Fragment>
          )})}
          {progressLinePath && (
            <div className="absolute top-0 left-0 pointer-events-none z-35" style={{ height: totalContentHeight, width: totalGridWidth ? `${totalGridWidth}px` : '100%' }}>
              <svg width={totalGridWidth || '100%'} height="100%"><path d={progressLinePath} stroke={progressLineColor} strokeWidth="2" fill="none" strokeDasharray="4 4" /></svg>
            </div>
          )}
        </div>
      </div>
      <button onClick={onAddTask} title={t('addTask')} className="fixed bottom-8 right-8 z-50 w-14 h-14 bg-indigo-600 text-white rounded-full flex items-center justify-center shadow-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all transform hover:scale-110">
        <PlusIcon className="w-7 h-7" />
      </button>
    </div>
  );
};

export default Timeline;

import React, { useState, useEffect, useRef, useCallback } from 'react';
import Timeline from './components/Timeline.tsx';
import { useLanguage } from './contexts/LocaleContext.tsx';
import { translations, TranslationKey } from './lib/locales.ts';
import { SettingsIcon, ZoomInIcon, ZoomOutIcon, ProgressLineIcon, XIcon, DownloadIcon, UploadIcon } from './components/Icons.tsx';
import SettingsDialog from './components/SettingsDialog.tsx';
import DatePicker from './components/DatePicker.tsx';
import { Task } from './types.ts';
import { formatDateUTC, addDaysUTC, parseUTCDateString, addWorkingDays, addOrSubtractWorkingDays, calculateWorkingDays } from './lib/utils.ts';
import ConfirmDialog from './components/ConfirmDialog.tsx';

const ZOOM_LEVELS = [8, 12, 18, 24, 40, 64];

const getInitialStartDate = () => {
    const now = new Date();
    const todayUTC = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
    return formatDateUTC(todayUTC);
};

const getInitialEndDate = () => {
    const now = new Date();
    const todayUTC = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate()));
    const futureDate = addDaysUTC(todayUTC, 60);
    return formatDateUTC(futureDate);
};

const INITIAL_COLORS = {
    base: '#fef08a',
    progress: '#bae6fd',
    text: '#000000',
    progressLine: '#ff0000',
};

const App: React.FC = () => {
  const { language, setLanguage } = useLanguage();
  const t = useCallback((key: TranslationKey) => {
    return translations[key][language];
  }, [language]);

  const [isSettingsModalOpen, setIsSettingsModalOpen] = useState(false);
  const [confirmModalState, setConfirmModalState] = useState<{ isOpen: boolean; title: string; message: string; onConfirm: () => void; }>({
    isOpen: false, title: '', message: '', onConfirm: () => {},
  });
  const [holidays, setHolidays] = useState<Set<number>>(() => new Set([0, 6]));
  const [columnVisibility, setColumnVisibility] = useState({
    assignee: true, startDate: false, endDate: false, duration: true, progress: true, manHours: false,
  });
  const [zoomIndex, setZoomIndex] = useState(3);
  const [progressLineDate, setProgressLineDate] = useState<string | null>(null);
  const [isProgressLineCalendarOpen, setIsProgressLineCalendarOpen] = useState(false);
  const importFileRef = useRef<HTMLInputElement>(null);
  const [rowHeight, setRowHeight] = useState<number>(40);

  const [projectName, setProjectName] = useState<string>(() => translations.untitledProject.ja);
  const [projectStart, setProjectStart] = useState<string>(getInitialStartDate());
  const [projectEnd, setProjectEnd] = useState<string>(getInitialEndDate());
  const [creationDate, setCreationDate] = useState<string>(getInitialStartDate());
  const [creatorName, setCreatorName] = useState<string>('');

  const [baseColor, setBaseColor] = useState<string>(INITIAL_COLORS.base);
  const [progressColor, setProgressColor] = useState<string>(INITIAL_COLORS.progress);
  const [textColor, setTextColor] = useState<string>(INITIAL_COLORS.text);
  const [progressLineColor, setProgressLineColor] = useState<string>(INITIAL_COLORS.progressLine);

  const [tasks, setTasks] = useState<Task[]>(() => {
    const initialT = (key: TranslationKey) => translations[key]['ja'];
    return [
      { id: '1', name: `${initialT('newTaskName')} 1`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
      { id: '2', name: `${initialT('newTaskName')} 2`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
      { id: '3', name: `${initialT('newTaskName')} 3`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 },
    ];
  });

  const handleZoomIn = () => setZoomIndex(prev => Math.min(prev + 1, ZOOM_LEVELS.length - 1));
  const handleZoomOut = () => setZoomIndex(prev => Math.max(prev - 1, 0));

  const toggleLanguage = () => {
    setLanguage(lang => (lang === 'ja' ? 'en' : 'ja'));
  };

  const handleResetColors = () => {
    setBaseColor(INITIAL_COLORS.base);
    setProgressColor(INITIAL_COLORS.progress);
    setTextColor(INITIAL_COLORS.text);
    setProgressLineColor(INITIAL_COLORS.progressLine);
  };

  const handleDeleteAllTasks = () => {
    if (tasks.length === 0) return;
    setConfirmModalState({
        isOpen: true,
        title: t('deleteAllTasks'),
        message: t('confirmDeleteAllTasks'),
        onConfirm: () => {
            setTasks([]);
            setConfirmModalState({ isOpen: false, title: '', message: '', onConfirm: () => {} });
        },
    });
  };

  const handleAddTask = () => {
    const newId = Date.now().toString();
    setTasks(prev => [...prev, { id: newId, name: `${t('newTaskName')} ${prev.length + 1}`, assignee: '', startDate: '', endDate: '', progress: 0, manHours: 0 }]);
  };

  const handleDeleteTask = (id: string) => {
    setTasks(prev => prev.filter(task => task.id !== id));
  };

  const handleDuplicateTask = (taskId: string) => {
    setTasks(currentTasks => {
      const taskIndex = currentTasks.findIndex(t => t.id === taskId);
      if (taskIndex === -1) return currentTasks;
      const originalTask = currentTasks[taskIndex];
      const newTask: Task = {
        ...originalTask,
        id: Date.now().toString(),
        name: `${originalTask.name}${t('copySuffix')}`,
      };
      const newTasks = [...currentTasks];
      newTasks.splice(taskIndex + 1, 0, newTask);
      return newTasks;
    });
  };

  const handleTaskChange = (id: string, field: keyof Task, value: string | number | undefined) => {
    setTasks(prev => prev.map(task => task.id === id ? { ...task, [field]: value } : task));
  };

  const handleDurationChange = (task: Task, newDurationStr: string) => {
    const newDuration = parseInt(newDurationStr, 10);
    if (isNaN(newDuration) || newDuration < 1) return;
    const startDate = parseUTCDateString(task.startDate);
    if (startDate) {
        const newEndDate = addWorkingDays(startDate, newDuration, holidays);
        handleTaskChange(task.id, 'endDate', formatDateUTC(newEndDate));
    }
  };

  const handleProgressChange = (taskId: string, newProgressStr: string) => {
    let newProgress = parseInt(newProgressStr, 10);
    if (isNaN(newProgress)) newProgress = 0;
    const clampedProgress = Math.max(0, Math.min(100, newProgress));
    handleTaskChange(taskId, 'progress', clampedProgress);
  };

  const handleManHoursChange = (taskId: string, newManHoursStr: string) => {
    let newManHours = parseFloat(newManHoursStr);
    if (isNaN(newManHours)) {
      handleTaskChange(taskId, 'manHours', undefined);
      return;
    }
    const clampedManHours = Math.max(0, newManHours);
    handleTaskChange(taskId, 'manHours', clampedManHours);
  };

  const handleTaskDateSet = (taskId: string, startDateStr: string, endDateStr: string) => {
      const d1 = parseUTCDateString(startDateStr);
      const d2 = parseUTCDateString(endDateStr);
      if (d1 && d2) {
          const finalStartDate = d1.getTime() < d2.getTime() ? startDateStr : endDateStr;
          const finalEndDate = d1.getTime() < d2.getTime() ? endDateStr : startDateStr;
          setTasks(prev => prev.map(task => task.id === taskId ? { ...task, startDate: finalStartDate, endDate: finalEndDate } : task));
      }
  };

  const handleTaskDragUpdate = (taskId: string, actionType: 'move' | 'resize-start' | 'resize-end', initialStartDate: Date, initialEndDate: Date, dayOffset: number) => {
      const pStart = parseUTCDateString(projectStart);
      const pEnd = parseUTCDateString(projectEnd);
      if (!pStart || !pEnd) return;

      setTasks(currentTasks =>
        currentTasks.map(task => {
          if (task.id === taskId) {
            let newStartDate: Date;
            let newEndDate: Date;
            switch (actionType) {
              case 'move': {
                const durationInWorkingDays = calculateWorkingDays(initialStartDate, initialEndDate, holidays) || 1;
                let potentialStartDate = addDaysUTC(initialStartDate, dayOffset);
                if (potentialStartDate.getTime() < pStart.getTime()) potentialStartDate = pStart;
                let potentialEndDate = addWorkingDays(potentialStartDate, durationInWorkingDays, holidays);
                if (potentialEndDate.getTime() > pEnd.getTime()) {
                  potentialEndDate = pEnd;
                  potentialStartDate = addOrSubtractWorkingDays(potentialEndDate, -(durationInWorkingDays - 1), holidays);
                  if (potentialStartDate.getTime() < pStart.getTime()) potentialStartDate = pStart;
                }
                newStartDate = potentialStartDate;
                newEndDate = addWorkingDays(newStartDate, durationInWorkingDays, holidays);
                if (newEndDate.getTime() > pEnd.getTime()) newEndDate = pEnd;
                if (newEndDate < newStartDate) newEndDate = newStartDate;
                break;
              }
              case 'resize-start': {
                newStartDate = addDaysUTC(initialStartDate, dayOffset);
                newEndDate = initialEndDate;
                if (newStartDate.getTime() < pStart.getTime()) newStartDate = pStart;
                if (newStartDate.getTime() > newEndDate.getTime()) [newStartDate, newEndDate] = [newEndDate, newStartDate];
                break;
              }
              case 'resize-end': {
                newStartDate = initialStartDate;
                newEndDate = addDaysUTC(initialEndDate, dayOffset);
                if (newEndDate.getTime() > pEnd.getTime()) newEndDate = pEnd;
                if (newEndDate.getTime() < newStartDate.getTime()) [newEndDate, newStartDate] = [newEndDate, newStartDate];
                break;
              }
            }
            return { ...task, startDate: formatDateUTC(newStartDate), endDate: formatDateUTC(newEndDate) };
          }
          return task;
        })
      );
  };

  const handleTaskReorder = (draggedTaskId: string, dropIndex: number) => {
      setTasks(currentTasks => {
          const newTasks = [...currentTasks];
          const draggedTask = newTasks.find(t => t.id === draggedTaskId);
          if (!draggedTask) return currentTasks;
          const draggedIndex = newTasks.indexOf(draggedTask);
          newTasks.splice(draggedIndex, 1);
          const adjustedDropIndex = dropIndex > draggedIndex ? dropIndex - 1 : dropIndex;
          newTasks.splice(adjustedDropIndex, 0, draggedTask);
          return newTasks;
      });
  };

  useEffect(() => {
    if (!isProgressLineCalendarOpen) return;
    const handleClickOutside = (event: MouseEvent) => {
        if (!(event.target as HTMLElement).closest('[data-calendar-popover]') && !(event.target as HTMLElement).closest('[data-calendar-toggle]')) {
             setIsProgressLineCalendarOpen(false);
        }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
        document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isProgressLineCalendarOpen]);

  const handleExport = () => {
    const dataToSave = {
        projectName, projectStart, projectEnd, creationDate, creatorName, tasks,
        settings: { holidays: Array.from(holidays), columnVisibility, baseColor, progressColor, textColor, rowHeight, progressLineColor }
    };
    const jsonString = JSON.stringify(dataToSave, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    const today = new Date();
    const formattedDate = `${today.getFullYear()}-${(today.getMonth() + 1).toString().padStart(2, '0')}-${today.getDate().toString().padStart(2, '0')}`;
    a.download = `Schedule_${formattedDate}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleImportClick = () => importFileRef.current?.click();

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      const file = event.target.files?.[0];
      if (!file) return;
      const reader = new FileReader();
      reader.onload = (e) => {
          try {
              const text = e.target?.result;
              if (typeof text !== 'string') throw new Error("File content is not a string");
              const data = JSON.parse(text);
              if (!data.projectName || !data.projectStart || !data.projectEnd || !Array.isArray(data.tasks)) throw new Error("Invalid file format");

              setProjectName(data.projectName);
              setProjectStart(data.projectStart);
              setProjectEnd(data.projectEnd);
              if (data.creationDate) setCreationDate(data.creationDate);
              if (data.creatorName) setCreatorName(data.creatorName);
              setTasks(data.tasks);

              if (data.settings) {
                if (data.settings.holidays) setHolidays(new Set(data.settings.holidays));
                if (data.settings.columnVisibility) setColumnVisibility(prev => ({...prev, ...data.settings.columnVisibility}));
                if (data.settings.baseColor) setBaseColor(data.settings.baseColor);
                if (data.settings.progressColor) setProgressColor(data.settings.progressColor);
                if (data.settings.textColor) setTextColor(data.settings.textColor);
                if (data.settings.rowHeight) setRowHeight(data.settings.rowHeight);
                if (data.settings.progressLineColor) setProgressLineColor(data.settings.progressLineColor);
              }
          } catch (error) {
              console.error("Failed to import file:", error);
              alert(t('importError'));
          }
      };
      reader.readAsText(file);
      event.target.value = '';
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800 font-sans">
      {isSettingsModalOpen && (
        <SettingsDialog
          onClose={() => setIsSettingsModalOpen(false)}
          holidays={holidays}
          setHolidays={setHolidays}
          columnVisibility={columnVisibility}
          setColumnVisibility={setColumnVisibility}
          baseColor={baseColor}
          setBaseColor={setBaseColor}
          progressColor={progressColor}
          setProgressColor={setProgressColor}
          textColor={textColor}
          setTextColor={setTextColor}
          progressLineColor={progressLineColor}
          setProgressLineColor={setProgressLineColor}
          rowHeight={rowHeight}
          setRowHeight={setRowHeight}
          onResetColors={handleResetColors}
        />
      )}
      {confirmModalState.isOpen && (
        <ConfirmDialog
          title={confirmModalState.title}
          message={confirmModalState.message}
          onConfirm={confirmModalState.onConfirm}
          onClose={() => setConfirmModalState({ isOpen: false, title: '', message: '', onConfirm: () => {} })}
          confirmText={t('delete')}
          cancelText={t('cancel')}
        />
      )}
      <header className="bg-white shadow-md sticky top-0 z-40">
        <div className="w-full px-4 py-4 flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-slate-700 tracking-tight">{t('ganttChartMaker')}</h1>
            <p className="text-slate-500 mt-1">{t('appDescription')}</p>
          </div>
          <div className="flex items-center space-x-2 sm:space-x-4">
            <div className="flex items-center justify-end space-x-2 p-1 sm:p-2 rounded-lg bg-slate-100 border border-slate-200">
                <button onClick={() => setIsSettingsModalOpen(true)} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('settings')}>
                    <SettingsIcon className="w-5 h-5" />
                </button>
                <div className="h-6 border-l border-gray-300"></div>
                <div className="relative">
                    <button data-calendar-toggle onClick={() => setIsProgressLineCalendarOpen(prev => !prev)} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 transition-colors" title={t('progressLine')}>
                        <ProgressLineIcon className="w-5 h-5" />
                    </button>
                    {isProgressLineCalendarOpen && (
                         <DatePicker
                            initialDate={progressLineDate || undefined}
                            onSelectDate={(date) => { setProgressLineDate(date); setIsProgressLineCalendarOpen(false); }}
                            onClose={() => setIsProgressLineCalendarOpen(false)}
                            minDate={projectStart}
                            maxDate={projectEnd}
                        />
                    )}
                </div>
                {progressLineDate && (
                    <button onClick={() => setProgressLineDate(null)} className="p-1 rounded-full text-red-500 bg-red-100 hover:bg-red-200 transition-colors" title={t('clearProgressLine')}>
                        <XIcon className="w-4 h-4" />
                    </button>
                )}
                <div className="h-6 border-l border-gray-300"></div>
                <button onClick={handleZoomOut} disabled={zoomIndex === 0} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors" title={t('zoomOut')}>
                    <ZoomOutIcon className="w-5 h-5" />
                </button>
                <button onClick={handleZoomIn} disabled={zoomIndex === ZOOM_LEVELS.length - 1} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors" title={t('zoomIn')}>
                    <ZoomInIcon className="w-5 h-5" />
                </button>
            </div>
            <div className="flex items-center justify-end space-x-2 p-1 sm:p-2 rounded-lg bg-slate-100 border border-slate-200">
                <button onClick={handleImportClick} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('importData')}>
                    <UploadIcon className="w-5 h-5" />
                </button>
                <input type="file" ref={importFileRef} onChange={handleFileChange} accept=".json, .gantt.json" className="hidden" />
                <div className="h-6 border-l border-gray-300"></div>
                <button onClick={handleExport} className="p-2 rounded-full text-gray-500 hover:bg-gray-200 hover:text-gray-700 transition-colors" title={t('exportData')}>
                    <DownloadIcon className="w-5 h-5" />
                </button>
            </div>
            <button onClick={toggleLanguage} className="px-4 py-2 border border-slate-300 rounded-md text-sm font-medium text-slate-600 hover:bg-slate-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors">
              {language === 'ja' ? 'English' : '日本語'}
            </button>
          </div>
        </div>
      </header>
      <main className="w-full p-2">
        <Timeline 
          projectName={projectName}
          setProjectName={setProjectName}
          projectStart={projectStart}
          setProjectStart={setProjectStart}
          projectEnd={projectEnd}
          setProjectEnd={setProjectEnd}
          creationDate={creationDate}
          setCreationDate={setCreationDate}
          creatorName={creatorName}
          setCreatorName={setCreatorName}
          tasks={tasks}
          holidays={holidays}
          columnVisibility={columnVisibility}
          zoomIndex={zoomIndex}
          progressLineDate={progressLineDate}
          baseColor={baseColor}
          progressColor={progressColor}
          textColor={textColor}
          progressLineColor={progressLineColor}
          rowHeight={rowHeight}
          onDeleteAllTasks={handleDeleteAllTasks}
          onAddTask={handleAddTask}
          onDeleteTask={handleDeleteTask}
          onDuplicateTask={handleDuplicateTask}
          onTaskChange={handleTaskChange}
          onDurationChange={handleDurationChange}
          onProgressChange={handleProgressChange}
          onManHoursChange={handleManHoursChange}
          onTaskDateSet={handleTaskDateSet}
          onTaskDragUpdate={handleTaskDragUpdate}
          onTaskReorder={handleTaskReorder}
        />
      </main>
    </div>
  );
};

export default App;

